import React from 'react';
import {Link} from 'react-router-dom'

const InvisiSelect = (props) => {
    return (
    // <div className='form-group'>
    //         <h1 className='text-center mt-0'>Select Game Mode</h1>
    //     <Link to='/'><button className='btn btn-lg btn-info btn-block mt-5' type="submit">vs. Computer</button></Link>
    //     <Link to='/'><button  className='btn btn-lg btn-success btn-block mb-5 mt-5' type="submit">vs. Player</button></Link>
    //     {localStorage.token ? <Link to='/' onClick={props.logout}><button id='exit' className='btn btn-lg btn-dark btn-block' type="submit">Logout</button></Link> : <Link to='/' onClick={props.logout}><button id='exit' className='btn btn-lg btn-dark btn-block' type="submit">Exit</button></Link> }
    //     <Link to='/Main'><button className='btn btn-lg btn-muted btn-block'>Go Back</button></Link>
    //     </div>
    <body className='text-center bg-light'>
    <form className='form-signin form-group'>
    <h1 className="h3 mb-3 font-weight-normal mt-5">Login or Sign-up</h1>
    <div className='d-inline-block'>
    <label for="inputUser" className="sr-only">Username</label>
    <input onChange={this.handleChange} type='text' id="inputUsername" className='form-group' name='username' placeholder='Username' required autoFocus></input>
    <div>
    <label for="inputPass" className="sr-only">Password</label>
    <input onChange={this.handleChange} type='text' id="inputPassword" className='form-group' name='password' placeholder='Password' required></input>
    </div>
        <div className='center'>
    <button onClick={this.handleSubmit} className='btn btn-lg btn-primary btn-block' type="submit">Log in</button>
    <Link to='/Register'><button className='btn btn-lg btn-success btn-block mt-1' type="submit">Register</button></Link>
    <Link to='/Main'><button className='btn btn-lg btn-secondary btn-block mt-5' type="submit">Just Play</button></Link>
    </div>
    </div>
    </form>
    </body>
    )
}

export default InvisiSelect
