import React, {Component} from 'react';
import TitleScreen from './ReactComponents/TitleScreen'
import InputManager from './InputManager';

const width = 800;
const height = window.innerHeight;
const ratio = window.devicePixelRatio || 1
const GameState = {
    StartScreen: 0,
    Playing: 1,
    GameOver: 2
}

class Canvas extends Component {
    constructor(){
        super();

        this.state = {
            input: new InputManager(),
            screen: {
                width: width,
                height: height,
                ratio: window.devicePixelRatio
            },
            score: 0,
            gameState: GameState.StartScreen,
            previousState: GameState.StartScreen,
            context: null
        }
        this.ship = null;
        this.invaders = [];
        this.lastStateChange = 0;
        this.previousDelta = 0;
        this.fpsLimit = 30;
        this.showControls = false;
    }
    componentDidMount(){
        this.state.input.bindKeys();
        requestAnimationFrame(() => {this.update()});
    }
    componentWillMount(){
        this.state.input.unbindKeys();
    }
    update(currentDelta) {
        requestAnimationFrame(() => {this.update()});
    }
    update = () => {
        const keys = this.state.input.pressedKeys;
        if (this.state.gameState === GameState.StartScreen && keys.enter){
            this.startGame();
        }
        requestAnimationFrame(() => {this.update()});
    }

    startGame = () => {
        this.setState({
            gameState: GameState.Playing
        });
    }
    render() {
        return (
            <div>
                {this.state.gameState === GameState.StartScreen && <TitleScreen />}
                <canvas ref="canvas" id='canvas' width={this.state.screen.width * this.state.screen.ratio}
            height={this.state.screen.height * this.state.screen.ratio}/>
            </div>
        );
    }
}

export default Canvas
